<?php

class Solution
{
    private $numbersCount = [], $answer = [];

    private function addAnswer($a, $b, $c)
    {
        if ($c < $b) {
            [$b, $c] = [$c, $b];
        }

        if ($c < $a) {
            [$a, $c] = [$c, $a];
        }

        if ($b < $a) {
            [$a, $b] = [$b, $a];
        }

        $this->answer["$a,$b,$c"] = true;
    }

    private function constructAnswer()
    {
        $finalAnswer = [];

        foreach ($this->answer as $key => $a) {
            $finalAnswer[] = array_map('intval', explode(",", $key));
        }

        return $finalAnswer;
    }

    private function checkAccordance($a, $b)
    {
        $this->numbersCount[$a]--;
        $this->numbersCount[$b]--;

        $thirdNumber = -$a - $b;

        $result = null;

        if (isset($this->numbersCount[$thirdNumber]) && $this->numbersCount[$thirdNumber] > 0) {
            $result = $thirdNumber;
        }

        $this->numbersCount[$a]++;
        $this->numbersCount[$b]++;

        return $result;
    }

    private function countNumbers(array $numbers)
    {
        foreach ($numbers as $number) {
            $this->numbersCount[$number] = ($this->numbersCount[$number] ?? 0) + 1;
        }
    }

    function threeSum($nums): array
    {
        $this->countNumbers($nums);

        for ($i = 0; $i < count($nums); $i++) {
            for ($j = 0; $j < $i; $j++) {
                if ($i === $j) {
                    continue;
                }

                $result = $this->checkAccordance($nums[$i], $nums[$j]);

                if (!is_null($result)) {
                    $this->addAnswer($nums[$i], $nums[$j], $result);
                }
            }
        }

        return $this->constructAnswer();
    }
}

